(function(){tinymce.PluginManager.requireLangPack('example');tinymce.create('tinymce.plugins.SimpleBrowser',{init:function(ed,url){ed.addCommand('mceSimpleBrowser',function(){ed.windowManager.open({file:url+'/dialog.php',width:500+parseInt(ed.getLang('example.delta_width',0)),height:500+parseInt(ed.getLang('example.delta_height',0)),inline:1},{plugin_url:url,some_custom_arg:'custom arg'})});ed.addButton('SimpleBrowser',{title:'Image Manager',cmd:'mceSimpleBrowser',image:url+'/img/example.gif'});ed.onNodeChange.add(function(ed,cm,n){cm.setActive('SimpleBrowser',n.nodeName=='IMG')})},createControl:function(n,cm){return null},getInfo:function(){return{longname:'SimpleBrowser Plugin',author:'Hapsoro Renaldy N <hapsoro.renaldy at yahoo.com>',authorurl:'N/A',infourl:'N/A',version:"1.0"}}});tinymce.PluginManager.add('SimpleBrowser',tinymce.plugins.SimpleBrowser)})();